import React, { FunctionComponent } from 'react';

import { ParamsTags } from './types';
import { useTranslate } from './useTranslate';

type TProps = {
  parameters?: ParamsTags;
  children?: string;
  noWrap?: boolean;
  /**
   * @deprecated Use noWrap to disable in-context wrapping
   */
  strategy?: 'ELEMENT_WRAP' | 'TEXT_WRAP' | 'NO_WRAP';
  keyName?: string;
};

export const T: FunctionComponent<TProps> = (props: TProps) => {
  const key = props.keyName || props.children;
  if (!key) {
    // eslint-disable-next-line no-console
    console.error('T component: keyName not defined');
  }
  const defaultValue = props.keyName ? props.children : undefined;

  const t = useTranslate();

  const translation = t({
    key,
    parameters: props.parameters,
    defaultValue,
    noWrap: props.noWrap,
  });

  return <>{translation}</>;
};
import { useEffect, useState } from 'react';
import { useTolgeeContext } from './useTolgeeContext';

/**
 * Custom react hook
 * @return function accepting language abbreviation as parameter
 */
export const useCurrentLanguage = () => {
  const context = useTolgeeContext();

  //to make react rerender components which are using current language
  const [language, setLanguage] = useState(context.tolgee.lang);

  useEffect(() => {
    const subscription = context.tolgee.onLangChange.subscribe((lang) => {
      setLanguage(lang);
    });
    return () => {
      subscription.unsubscribe();
    };
  }, []);

  return () => {
    return language;
  };
};
